            if {[catch { FLOW::create_related {
                                proto 6
                                clientflow [IP::remote_addr] 1221 [LINK::vlan_id]
                                serverflow [clientside {IP::remote_addr}] 1221 [clientside {LINK::vlan_id}]
                            } } result] } {
