                MQTT::respond type PUBACK packet_id $pktid
            } elseif { $in_qos == 2 } {
                MQTT::respond type PUBREC packet_id $pktid
            }
        }
        "PUBREL" {
            set pktid [MQTT::packet_id]
            MQTT::drop
            MQTT::respond type PUBCOMP packet_id $pktid
        }
    }
}

