         if { $suback_count == 0 } {
             set count [table lookup -subtable "outstanding_packetid_table" "[client_addr]_[client_port]_${packetid}"]
             if { $count != "" } {
                set suback_count $count
             } elseif { [llength $rclist] > 0 } {
                MQTT::replace type SUBACK return_code_list $rclist
                set rclist [list]
